mapper.select:
SELECT e."id", e."pid", e."comentAt", e."comentBy",ue.username comentByC, e."cotent", e."approuverNum", partager.transmettreNum, collec.colletionNum,commenter.commenterNum, e."title", f."refId",f."refType",f.filename,f.url,isPartaager.todayIsTransmettre,IsColletion.todayIsColletion,isAprouver.todayIsAprouver FROM "xacx_information_push" e LEFT JOIN "xacx_file" f on f."refId" = e."id" and f."refType" = 'informationpushFile' and f."sign" = 'add' left join (select count(*) as transmettreNum ,"pushId" from xacx_information_push_partager group by "pushId") partager on e.id = partager."pushId" left join (select count(*) as commenterNum ,"pushId" from xacx_information_push_commenter group by "pushId") commenter on e.id = commenter."pushId" left join (select count(*) as colletionNum ,"comentId" from xacx_information_push_collection group by "comentId") collec on e.id = collec."comentId" left join (select count(*) as todayIsTransmettre,"partagerBy","pushId" from "xacx_information_push_partager" where to_date(cast("partagerAt" as TEXT),'yyyy-mm-dd') = current_date and "partagerBy" = #{userid} group by "partagerBy","pushId" ) isPartaager on isPartaager."pushId" = e.id left join (select count(*) as todayIsColletion,"collectBy","comentId" from "xacx_information_push_collection" where to_date(cast("collectAt" as TEXT),'yyyy-mm-dd') = current_date and "collectBy" = #{userid} group by "collectBy","comentId" ) IsColletion on IsColletion."comentId" = e.id left join (select count(*) as todayIsAprouver,"approuverBy","comentId" from "xacx_information_push_approuver" where to_date(cast("approuverAt" as TEXT),'yyyy-mm-dd') = current_date and "approuverBy" = #{userid} group by "approuverBy","comentId"
) isAprouver on e.id = isAprouver."comentId"
left join xacx_user ue on ue.pernr = e."comentBy" WHERE e."id" = #{id} ;

SELECT e."id", e."pid", e."comentAt", e."comentBy",eu.username comentByC, e."cotent", e."approuverNum", partager.transmettreNum, collec.colletionNum,commenter.commenterNum, e."title", f."refId",f."refType",f.filename,f.url,f."fileType",isPartaager.todayIsTransmettre,IsColletion.todayIsColletion,isAprouver.todayIsAprouver FROM "xacx_information_push" e LEFT JOIN "xacx_file" f on f."refId" = e."id" and f."refType" = 'informationpushFile' and f."sign" = 'add' left join (select count(*) as transmettreNum ,"pushId" from xacx_information_push_partager group by "pushId") partager on e.id = partager."pushId" left join (select count(*) as commenterNum ,"pushId" from xacx_information_push_commenter where (pid is null or pid ='') group by "pushId" ) commenter on e.id = commenter."pushId" left join (select count(*) as colletionNum ,"comentId" from xacx_information_push_collection group by "comentId") collec on e.id = collec."comentId" left join (select count(*) as todayIsTransmettre,"partagerBy","pushId" from "xacx_information_push_partager" where to_date(cast("partagerAt" as TEXT),'yyyy-mm-dd') = current_date and "partagerBy" = #{userid} group by "partagerBy","pushId" ) isPartaager on isPartaager."pushId" = e.id left join (select count(*) as todayIsColletion,"collectBy","comentId" from "xacx_information_push_collection" where to_date(cast("collectAt" as TEXT),'yyyy-mm-dd') = current_date and "collectBy" = #{userid} group by "collectBy","comentId" ) IsColletion on IsColletion."comentId" = e.id left join (select count(*) as todayIsAprouver,"approuverBy","comentId" from "xacx_information_push_approuver" where to_date(cast("approuverAt" as TEXT),'yyyy-mm-dd') = current_date and "approuverBy" = #{userid} group by "approuverBy","comentId"
) isAprouver on e.id = isAprouver."comentId" left join xacx_user eu on eu.pernr = e."comentBy"
order by e."comentAt" desc
