mapper.select:
WITH RECURSIVE T ("Id", "Pid", "RightName", PATH, DEPTH) AS ( SELECT "Id", "Pid", "RightName", "Id"||'/' AS PATH, 1 AS DEPTH FROM app_right
"Pid" = '0'
and "RightType" = #{rightType}
UNION ALL
SELECT D."Id",D."Pid", D."RightName", T.PATH || D."Id", T.DEPTH + 1 AS DEPTH FROM app_right D JOIN T ON D."Pid" = T."Id" ) SELECT "Id", "Pid", "RightName", PATH, DEPTH FROM T ORDER BY PATH;
